    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        if (jCheckBox1.isSelected()) {
            if (new Setup().IsAccept() == 1) {
            try{
             Process pr=Runtime.getRuntime().exec("cmd /c del /Q /F C:\\cs_system\\bin\\import\\rs.bat");
             pr.destroy();
                
            }catch(Exception e){
                e.printStackTrace();
            }
               
                new LOGIN_FRAME().setVisible(true);
                this.dispose();
            } else {
                JOptionPane.showMessageDialog(this, "Unknown error occured.Please contact support.");
            }

        } else {
            JOptionPane.showMessageDialog(this, "Please accept agreement befor procceding");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

